/* FAQs with accordion - START */

.page-content {
	width: 100%;
	max-width: 1274px;
}
.faq-tabs {
	margin: 0 -11px;
}
.faq-tabs ul {
	list-style: none;
	display: flex;
	flex-wrap: wrap;
	width: 100%;
	background-color: #fff;
}
.faq-tabs ul li {
	width: 33.33%;
	padding: 11px;
}
.faq-tabs ul li .box {
	height: 139px;
	background: #FFFFFF;
	border: 1px solid #C1C1C1;
	display: flex;
	align-items: center;
	cursor: pointer;
	transition: all 0.2s ease-out;
}
.faq-tabs .box .icon {
	width: 87px;
	height: 87px;
	background-color: #7E99AA;
	display: flex;
	justify-content: center;
	align-items: center;
	border-radius: 50%;
	margin: 0 46px;
	flex-shrink: 0;
	padding: 12px;
}
.faq-tabs .box .title {
	font-family: Montserrat, sans-serif;
	font-style: normal;
	font-weight: 500;
	font-size: 14px;
	line-height: 22px;
	color: #505050;
	text-align: left;
}
.faq-tabs ul li .box:hover {
	background-color: #F3F3F3;
}
.faq-tabs ul li .box.active {
	background-color: #F3F3F3;
	border-color: #7e99aa;
}
.faq-tabs .box:hover .title,
.faq-tabs .box.active .title{
	color: #000;
}
.selected-tab {
	text-align: center;
	border-bottom: 1px solid #C1C1C1;
	margin-bottom: 60px;
}
.selected-tab .tab-title {
    padding: 0 30px;
    margin: 0 auto;
    display: inline-block;
    top: 15px;
    position: relative;
    background-color: #fff;
    font-family: Montserrat;
    font-style: normal;
    font-weight: 600;
    font-size: 18px;
    color: #000000;
}
.tab-container {
	display: none;
	max-width: 873px;
    width: 100%;
    margin: 0 auto 90px;
}
.tab-container.active {
	display: block;
}
.acc-panel {
    border-bottom: 1px solid #DBDBDB;
}
.acc-header {
	font-family: Montserrat;
	font-weight: 600;
	font-size: 14px;
	color: #000000;
	display: flex;
    align-items: center;
    padding: 20px 20px 20px 0;
    cursor: pointer;
}
.acc-header:before {
	content: '';
	display: inline-block;
	background-repeat: no-repeat;
	margin-right: 20px;
	width: 19px;
	height: 19px;
	flex-shrink: 0;
	background-size: 100%;
	background-position: center;
	background-image: url("data:image/svg+xml,%3Csvg width='19' height='19' viewBox='0 0 19 19' fill='none' xmlns='http://www.w3.org/2000/svg'%3E%3Cline y1='9.5' x2='19' y2='9.5' stroke='black'/%3E%3Cline x1='9.5' y1='2.30699e-08' x2='9.5' y2='19' stroke='black'/%3E%3C/svg%3E%0A");
}
.acc-header.is-open:before {
	background-image: url("data:image/svg+xml,%3Csvg width='19' height='1' viewBox='0 0 19 1' fill='none' xmlns='http://www.w3.org/2000/svg'%3E%3Cline y1='0.5' x2='19' y2='0.5' stroke='black'/%3E%3C/svg%3E%0A");
}
.acc-content {
	font-family: Montserrat;
	font-size: 12px;
	color: #050505;
	padding: 0 20px 20px 39px;
	display: none;
}
.acc-header.is-open + .acc-content {
	display: block;
}
@media (max-width: 1200px) {
	.faq-tabs .box .icon {
		margin: 0 26px;
	}
}
@media (max-width: 1024px) {
	.faq-tabs ul {
		padding: 20px 0;
	}
	.faq-tabs ul li {
		width: 33.33%;
		padding: 5px 11px;
	}
	.faq-tabs ul li .box {
		height: 76px;
		padding: 8px;
	}
